import React, { useEffect, useState } from "react";
import { getAllSessions } from "../agents/tracking_agent/session_logger";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer
} from "recharts";
console.log("üìä All sessions:", sessions.length);
export default function ProgressDashboard({ patientName }) {
  const [sessions, setSessions] = useState([]);

  useEffect(() => {
    const data = getAllSessions(patientName);
    setSessions(data);
  }, [patientName]);

  if (!sessions || sessions.length < 2) {
    return (
      <div className="bg-white p-4 rounded-lg shadow mt-6 text-center text-sm text-gray-500">
        Not enough session data yet to visualize progress.
      </div>
    );
  }



  const chartData = sessions.map((s, i) => ({
    name: `S${i + 1}`,
    speed: s.strokeSpeedData?.averageSpeed ?? 0,
    tremor: s.tremorData?.tremorScore ?? 0,
    shrink: s.micrographiaData?.shrinkRatio ?? 0,
    accuracy: s.digitAccuracy ?? 0,
    timestamp: s.timestamp || null
  }));

  const CustomTooltip = ({ active, payload, label }) => {
    if (!active || !payload || payload.length === 0) return null;

    const point = payload[0].payload;
    const date = point.timestamp ? new Date(point.timestamp) : null;

    return (
      <div className="bg-white rounded-lg shadow p-2 border text-sm">
        <p><strong>{label}</strong></p>
        {date && (
          <>
            <p>üìÖ {date.toLocaleDateString()}</p>
            <p>‚è∞ {date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
          </>
        )}
        <p>üöÄ Speed: {point.speed.toFixed(2)} px/s</p>
        <p>üéØ Accuracy: {(point.accuracy * 100).toFixed(1)}%</p>
      </div>
    );
  };

  return (
    <div className="bg-white p-4 rounded-xl shadow mt-6">
      <h3 className="text-lg font-bold text-green-800 mb-4">üìà Therapy Progress</h3>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={chartData}>
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip content={<CustomTooltip />} />
          <Legend />
          <Line type="monotone" dataKey="speed" stroke="#8884d8" name="Avg Speed" />
          <Line type="monotone" dataKey="tremor" stroke="#82ca9d" name="Tremor Score" />
          <Line type="monotone" dataKey="shrink" stroke="#ffc658" name="Micrographia Ratio" />
          <Line type="monotone" dataKey="accuracy" stroke="#ff6b6b" name="Digit Accuracy" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}
