import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import ResultsTabs from "../components/ResultsTabs";
import ReplaySection from "../components/ReplaySection";
import { generatePDFReport, generateJSONReport } from "../agents/tracking_agent/report_generator";
import { saveAs } from "file-saver";

const motivationalQuotes = [
  "Your progress is beautiful. 🌸",
  "You’re doing better than you think. 🌟",
  "Small steps still move you forward. 🐾",
  "Your efforts matter. 💖",
];

function ResultsPage() {
  const location = useLocation();
  const sessionResults = location.state?.sessionData || JSON.parse(localStorage.getItem("lastSessionResults"));

  const [activeTab, setActiveTab] = useState("core");
  const [quote, setQuote] = useState(motivationalQuotes[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      const random = Math.floor(Math.random() * motivationalQuotes.length);
      setQuote(motivationalQuotes[random]);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  if (!sessionResults) {
    return <div className="text-center mt-10 text-red-600">No session results available.</div>;
  }

  const { metrics, fatigueInfo, patientName, sessionId } = sessionResults;

  const downloadPDF = async () => {
    const doc = await generatePDFReport(metrics, fatigueInfo, {
      autoSave: false,
      patientName,
      sessionId,
    });
    doc.save(`therapy_session_${sessionId}.pdf`);
  };

  const downloadJSON = async () => {
    const url = await generateJSONReport(metrics, fatigueInfo);
    saveAs(url, `therapy_session_${sessionId}.json`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-yellow-100 to-pink-100 p-4">
      <div className="text-center bg-white/60 border border-white backdrop-blur-md shadow-sm px-6 py-2 rounded-full text-sm font-medium text-gray-800 animate-pulse mb-6">
        ✨ {quote}
      </div>

      <div className="max-w-6xl mx-auto bg-white rounded-xl shadow-lg p-8">
        <h2 className="text-2xl font-bold text-blue-800 mb-6">🧾 Session Results for {patientName}</h2>

        <ResultsTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="mt-6">
          {activeTab === "core" && (
            <div className="space-y-2 text-gray-800">
              <p><strong>🌀 Average Speed:</strong> {metrics?.avgSpeed?.toFixed(2) || 0} px/sec</p>
              <p><strong>✍️ Average Pressure:</strong> {metrics?.avgPressure?.toFixed(2) || 0}</p>
              <p><strong>🎯 Stroke Stability:</strong> {metrics?.speedVariance?.toFixed(2) || 0}</p>
              <p><strong>🖌️ Total Strokes:</strong> {metrics?.totalStrokes || 0}</p>
            </div>
          )}

          {activeTab === "feedback" && (
            <div className="text-gray-800 space-y-2">
              <p>🧠 <strong>Therapist-style feedback:</strong> You maintained consistent pressure and speed in most digits.</p>
              <p>📈 <strong>Suggestion:</strong> Practice digits with curves (like 3, 5, 8) to improve fluidity.</p>
              <p>✅ <strong>Encouragement:</strong> Keep up the steady work and try to relax your wrist!</p>
            </div>
          )}

          {activeTab === "summary" && (
            <div className="text-gray-800 space-y-2">
              <p><strong>Session ID:</strong> {sessionId}</p>
              <p><strong>Fatigue Detected:</strong> {fatigueInfo?.detected ? "Yes" : "No"}</p>
              <p><strong>Reason:</strong> {fatigueInfo?.reason || "N/A"}</p>
              <p><strong>Timestamp:</strong> {new Date().toLocaleString()}</p>
            </div>
          )}
        </div>

        <ReplaySection
          attempts={metrics?.attempts || []}
          metrics={metrics}
          fatigue={fatigueInfo}
          patientName={patientName}
          sessionId={sessionId}
        />

        <div className="mt-6 flex flex-col sm:flex-row gap-4 justify-center">
          <button
            onClick={downloadPDF}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-full shadow-md"
          >
            📄 Download PDF Report
          </button>
          <button
            onClick={downloadJSON}
            className="bg-gray-500 hover:bg-gray-600 text-white px-6 py-2 rounded-full shadow-md"
          >
            📁 Download JSON Summary
          </button>
        </div>

        {/* Decorative footer */}
        <div className="relative w-full h-40 mt-12 overflow-hidden">
          <img src="/assets/grass.png" alt="Grass" className="absolute bottom-0 w-full object-cover z-0" />
          <img src="/assets/Walking_Bird.gif" alt="Bird walking" className="absolute bottom-4 left-4 h-20 animate-wiggle" />
          <img src="/assets/flowers/BlueFlower.png" alt="Blue Flower" className="absolute bottom-4 left-32 h-12 animate-bounce-slow" />
          <img src="/assets/flowers/PinkFlower.png" alt="Pink Flower" className="absolute bottom-4 left-64 h-10 animate-float-soft" />
          <img src="/assets/flowers/OrangeFlower.png" alt="Orange Flower" className="absolute bottom-4 left-[40%] h-14 animate-bounce-slow" />
          <img src="/assets/flowers/PurpleFlower.png" alt="Purple Flower" className="absolute bottom-4 right-32 h-12 animate-float-soft" />
          <img src="/assets/flowers/YellowFlower.png" alt="Yellow Flower" className="absolute bottom-4 right-64 h-10 animate-bounce-slow" />
        </div>
      </div>
    </div>
  );
}

export default ResultsPage;
