// frontend/src/agents/tracking_agent/session_logger.js

const STORAGE_KEY = "attempts";

// ðŸ” 1. Log individual drawing attempts
export function logAttempt(metricsObj) {
  const existing = JSON.parse(localStorage.getItem(STORAGE_KEY) || "[]");
  existing.push(metricsObj);
  localStorage.setItem(STORAGE_KEY, JSON.stringify(existing));
}

// frontend/src/agents/tracking_agent/session_logger.js

// âœ… Log a drawing session per patient (clean new logic)
export function saveSessionResult(patientName, sessionData) {
  const cleanName = patientName.trim().toLowerCase();
  const key = `sessions_${cleanName}`;

  const existing = JSON.parse(localStorage.getItem(key) || "[]");

  const alreadyExists = existing.some(e => e.sessionId === sessionData.sessionId);
  if (alreadyExists) return;

  existing.push(sessionData);
  localStorage.setItem(key, JSON.stringify(existing));
}

// âœ… Get all saved sessions for a patient
export function getAllSessions(patientName) {
  const cleanName = patientName.trim().toLowerCase();
  const key = `sessions_${cleanName}`;

  const sessions = JSON.parse(localStorage.getItem(key) || "[]");
  console.log(`ðŸ“¦ Loaded ${sessions.length} sessions for ${cleanName}`);
  return sessions;
}

// âœ… Generate a basic progress comparison against the previous session
export function getSessionProgressReport(currentMetrics, patientName) {
  const sessions = getAllSessions(patientName);
  const prev = sessions.at(-2); // second last

  if (!prev) return null;

  return {
    speedChange: currentMetrics.avgSpeed - prev.metrics.avgSpeed,
    tremorChange: currentMetrics.tremorData?.tremorScore - prev.tremorData?.tremorScore,
    accuracyChange: currentMetrics.digitAccuracy - prev.digitAccuracy,
    improvement: currentMetrics.digitAccuracy > prev.digitAccuracy,
  };
}
