// src/pages/ResultsPage.jsx
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import ResultsTabs from "../components/ResultsTabs";
import ReplaySection from "../components/ReplaySection";
import { generatePDFReport, generateJSONReport } from "../agents/tracking_agent/report_generator";
import { saveAs } from "file-saver";

const motivationalQuotes = [
  "Your progress is beautiful. üå∏",
  "You‚Äôre doing better than you think. üåü",
  "Small steps still move you forward. üêæ",
  "Your efforts matter. üíñ"
];

function ResultsPage() {
  const location = useLocation();
  const sessionResults = location.state?.sessionData; // ‚úÖ Fix

  const [activeTab, setActiveTab] = useState("core");
  const [quote, setQuote] = useState(motivationalQuotes[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      const random = Math.floor(Math.random() * motivationalQuotes.length);
      setQuote(motivationalQuotes[random]);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  if (!sessionResults || !sessionResults.metrics || !sessionResults.fatigueInfo) {
    return <div className="text-center mt-10 text-red-600">No session results available.</div>;
  }

  const { metrics, fatigueInfo, patientName, sessionId } = sessionResults;

  const downloadPDF = async () => {
    const doc = await generatePDFReport(metrics, fatigueInfo, {
      autoSave: false,
      patientName,
      sessionId,
    });
    doc.save(`therapy_session_${sessionId}.pdf`);
  };

  const downloadJSON = async () => {
    const url = await generateJSONReport(metrics, fatigueInfo);
    saveAs(url, `therapy_session_${sessionId}.json`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-yellow-100 to-pink-100 p-4">
      <div className="text-center bg-white/60 border border-white backdrop-blur-md shadow-sm px-6 py-2 rounded-full text-sm font-medium text-gray-800 animate-pulse mb-6">
        ‚ú® {quote}
      </div>

      <div className="max-w-6xl mx-auto bg-white rounded-xl shadow-lg p-8">
        <h2 className="text-2xl font-bold text-blue-800 mb-6">üßæ Session Results for {patientName}</h2>

        <ResultsTabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="mt-6">
          {activeTab === "core" && (
            <div className="space-y-2 text-gray-800">
              <p><strong>üåÄ Average Speed:</strong> {metrics.avgSpeed.toFixed(2)} px/sec</p>
              <p><strong>‚úçÔ∏è Average Pressure:</strong> {metrics.avgPressure.toFixed(2)}</p>
              <p><strong>üéØ Stroke Stability:</strong> {metrics.speedVariance.toFixed(2)}</p>
              <p><strong>üñåÔ∏è Total Strokes:</strong> {metrics.totalStrokes}</p>
            </div>
          )}

          {activeTab === "feedback" && (
            <div className="text-gray-800 space-y-2">
              <p>üß† <strong>Therapist-style feedback:</strong> You maintained consistent pressure and speed in most digits.</p>
              <p>üìà <strong>Suggestion:</strong> Practice digits with curves (like 3, 5, 8) to improve fluidity.</p>
              <p>‚úÖ <strong>Encouragement:</strong> Keep up the steady work and try to relax your wrist!</p>
            </div>
          )}

          {activeTab === "summary" && (
            <div className="text-gray-800 space-y-2">
              <p><strong>Session ID:</strong> {sessionId}</p>
              <p><strong>Fatigue Detected:</strong> {fatigueInfo.detected ? "Yes" : "No"}</p>
              <p><strong>Reason:</strong> {fatigueInfo.reason}</p>
              <p><strong>Timestamp:</strong> {new Date().toLocaleString()}</p>
            </div>
          )}
        </div>

        <ReplaySection />

        <div className="mt-6 flex flex-col sm:flex-row gap-4 justify-center">
          <button
            onClick={downloadPDF}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-full shadow-md"
          >
            üìÑ Download PDF Report
          </button>
          <button
            onClick={downloadJSON}
            className="bg-gray-500 hover:bg-gray-600 text-white px-6 py-2 rounded-full shadow-md"
          >
            üìÅ Download JSON Summary
          </button>
        </div>
      </div>
    </div>
  );
}

export default ResultsPage;
