// agents/cnnAgent.js
import * as tf from '@tensorflow/tfjs';

let model = null;
let currentLanguage = null;

export async function loadModel(lang = "en") {
  if (model && currentLanguage === lang) return model;

  const path = lang === "ar"
    ? "/models/arabic_model/model.json"
    : "/models/Final_PreluModel/model.json";

  try {
    model = await tf.loadLayersModel(path);
    currentLanguage = lang;
    console.log(`✅ ${lang.toUpperCase()} model loaded.`);
  } catch (error) {
    console.error(`❌ Failed to load ${lang} model:`, error);
    throw error;
  }

  return model;
}

export async function predictDigit(dataURL, lang = "en") {
  await loadModel(lang);

  const { tensor, debugCanvas } = await preprocessDrawing(dataURL, lang === "en");
  const prediction = model.predict(tensor);
  const predictionArray = await prediction.array();
  const predictedDigit = predictionArray[0].indexOf(Math.max(...predictionArray[0]));

  return { predictedDigit, debugCanvas };
}

export async function preprocessDrawing(dataURL, invert = true) {
  // Step 1: Draw image into temporary 28x28 canvas
  const tempCanvas = document.createElement("canvas");
  tempCanvas.width = 28;
  tempCanvas.height = 28;

  const ctx = tempCanvas.getContext("2d");

  const img = new Image();
  img.src = dataURL;
  await new Promise((res) => (img.onload = res));
  ctx.drawImage(img, 0, 0, 28, 28);

  // Step 2: Safely process the image into tensor, normalized, expanded
  const imageTensor = tf.browser.fromPixels(tempCanvas, 1).toFloat();
  const tensor = invert ? tf.sub(255, imageTensor) : imageTensor;
  const normalized = tensor.div(255.0);
  const expanded = normalized.expandDims(0);

  // Step 3: Generate debug canvas safely
  const debugCanvas = document.createElement("canvas");
  debugCanvas.width = 28;
  debugCanvas.height = 28;
  await tf.browser.toPixels(normalized.squeeze(), debugCanvas); // ✅ Now it works

  return { tensor: expanded, debugCanvas };
}
