// File: frontend/src/agents/cnnAgent.js
import * as tf from '@tensorflow/tfjs';

let model = null;
let currentLanguage = null;

export async function loadModel(lang = "en") {
  if (model && currentLanguage === lang) return model;

  const path = lang === "ar"
    ? "/models/model_tfjs_hala/model_tfjs/model.json"
    : "/models/ENGLISH/model.json";

  try {
    model = await tf.loadLayersModel(path);
    currentLanguage = lang;
    console.log(`âœ… ${lang.toUpperCase()} model loaded.`);
  } catch (error) {
    console.error(`âŒ Failed to load ${lang} model:`, error);
    throw error;
  }

  return model;
}

export async function predictDigit(dataURL, lang = "en") {
  await loadModel(lang);

  const tensor = await preprocessDrawing(dataURL);
  const prediction = model.predict(tensor);
  const predictionArray = await prediction.array();
  const predictedDigit = predictionArray[0].indexOf(Math.max(...predictionArray[0]));

  return predictedDigit;
}

export async function preprocessDrawing(dataURL) {
  const image = new Image();
  image.src = dataURL;
  await new Promise((res) => (image.onload = res));

  const canvas = document.createElement("canvas");
  canvas.width = image.width;
  canvas.height = image.height;
  const ctx = canvas.getContext("2d");
  ctx.drawImage(image, 0, 0);

  return await preprocessCanvas(canvas);
}

export async function preprocessCanvas(canvas) {
  const resized = document.createElement("canvas");
  resized.width = 28;
  resized.height = 28;
  const resizedCtx = resized.getContext("2d");
  resizedCtx.drawImage(canvas, 0, 0, 28, 28);

  const imgData = resizedCtx.getImageData(0, 0, 28, 28);
  const data = imgData.data;
  const gray = [];
  for (let i = 0; i < data.length; i += 4) {
    const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
    gray.push(1.0 - avg / 255); // grayscale + invert
  }

  let tensor = tf.tensor2d(gray, [28, 28]);

  tensor = centerImage(tensor);
  tensor = gaussianSmooth(tensor);

  const input = tensor.reshape([1, 28, 28, 1]);

  if (process.env.NODE_ENV === "development") {
    const debugCanvas = document.createElement("canvas");
    debugCanvas.width = 28;
    debugCanvas.height = 28;
    await tf.browser.toPixels(tensor, debugCanvas);
    console.log("ðŸ§ª Preprocessed image base64:", debugCanvas.toDataURL("image/png"));
  }

  return input;
}

function centerImage(tensor) {
  const sum = tensor.sum().arraySync();
  if (sum === 0) return tensor;

  const arr = tensor.arraySync();
  let cx = 0, cy = 0;

  for (let y = 0; y < 28; y++) {
    for (let x = 0; x < 28; x++) {
      const val = arr[y][x];
      cx += x * val;
      cy += y * val;
    }
  }

  cx /= sum;
  cy /= sum;

  const shiftX = Math.round(14 - cx);
  const shiftY = Math.round(14 - cy);

  // Pad the image to 30x30 to allow shifting without overflow
  let padded = tf.pad(tensor, [[1, 1], [1, 1]], 0);

  // Crop the padded image to apply the shift
  const begin = [1 - shiftY, 1 - shiftX];
  const size = [28, 28];

  // Prevent out-of-bounds cropping by clamping indices
  const safeBegin = begin.map(v => Math.max(0, Math.min(2, v)));

  return tf.slice(padded, safeBegin, size);
}


function gaussianSmooth(tensor) {
  const kernel = tf.tensor2d([
    [1, 2, 1],
    [2, 4, 2],
    [1, 2, 1]
  ]).div(16).reshape([3, 3, 1, 1]);

  const input = tensor.reshape([1, 28, 28, 1]);
  const blurred = tf.conv2d(input, kernel, 1, 'same');
  return blurred.reshape([28, 28]);
}
