// frontend/src/agents/tracking_agent/index.js

let canvasElement = null;
let ctx = null;
let strokes = [];
let currentStroke = [];
let isDrawing = false;
let sessionMeta = { patientName: "Anonymous", sessionId: "" };

function startListening() {
  if (!canvasElement) {
    console.warn("âš ï¸ No canvasElement set before startListening.");
    return;
  }

  canvasElement.addEventListener("mousedown", (e) => {
    isDrawing = true;
    currentStroke = [[e.offsetX, e.offsetY]];
  });

  canvasElement.addEventListener("mousemove", (e) => {
    if (isDrawing) {
      currentStroke.push([e.offsetX, e.offsetY]);
      ctx.lineTo(e.offsetX, e.offsetY);
      ctx.stroke();
    }
  });

  canvasElement.addEventListener("mouseup", () => {
    if (isDrawing && currentStroke.length > 0) {
      strokes.push(currentStroke);
    }
    isDrawing = false;
  });
}

function waitForCanvasAndInit() {
  const check = setInterval(() => {
    const found = document.querySelector("canvas");
    if (found) {
      clearInterval(check);
      canvasElement = found;
      ctx = canvasElement.getContext("2d");
      ctx.strokeStyle = "#000";
      ctx.lineWidth = 2;
      console.log("âœ… Tracking canvas detected.");
      startListening();
    }
  }, 300);
}

export function startTracking({ patientName = "Anonymous", sessionId = "" }) {
  sessionMeta = { patientName, sessionId };
  strokes = [];
  waitForCanvasAndInit();
  console.log("ðŸ§  Tracking started for", patientName);
}

export async function endTracking({
  generateJSON = true,
  autoDownloadLog = true,
}) {
  const result = {
    patientName: sessionMeta.patientName,
    sessionId: sessionMeta.sessionId,
    timestamp: new Date().toISOString(),
    strokeCount: strokes.length,
    strokes,
  };

  console.log("ðŸ“ˆ Session Stroke Data:", result);

  if (generateJSON && autoDownloadLog) {
    const blob = new Blob([JSON.stringify(result, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${sessionMeta.patientName}_tracking_log.json`;
    a.click();
    URL.revokeObjectURL(url);
  }

  return result;
}
