import React, { useRef, useEffect, useState } from "react";
import { toast } from "react-hot-toast";
import { ClipLoader } from "react-spinners";
import successSound from "../assets/success-chime.mp3";
import { startTracking } from "../agents/tracking_agent";
import { logAttempt } from "../agents/tracking_agent/session_logger";

function CanvasDrawing({ mcp, patientName, sessionManager, onComplete }) {
  const canvasRef = useRef(null);
  const [processing, setProcessing] = useState(false);
  const [predictionCount, setPredictionCount] = useState(0);
  const [canvasReady, setCanvasReady] = useState(false);
  const [hasDrawn, setHasDrawn] = useState(false);
  const [floatingDigit, setFloatingDigit] = useState(null);
  const successAudio = new Audio(successSound);
  const currentDigit = sessionManager.getCurrentDigit();

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    // Explicit size and style to avoid scaling issues
    const size = 300;
    canvas.width = size;
    canvas.height = size;
    canvas.style.width = `${size}px`;
    canvas.style.height = `${size}px`;

    const ctx = canvas.getContext("2d");
    if (!ctx) {
      console.error("❌ Failed to get 2D context");
      return;
    }

    // Clear and set background
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvas.width, canvas.height);
    ctx.strokeStyle = "red"; // RED for debug
    ctx.lineWidth = 5;
    ctx.lineCap = "round";

    let drawing = false;

    const startDrawing = (e) => {
      drawing = true;
      setHasDrawn(true);
      ctx.beginPath();
      ctx.moveTo(e.offsetX, e.offsetY);
      startTracking({ patientName });
    };

    const draw = (e) => {
      if (!drawing) return;
      console.log("Drawing at:", e.offsetX, e.offsetY);
      ctx.lineTo(e.offsetX, e.offsetY);
      ctx.stroke();
    };

    const stopDrawing = () => {
      drawing = false;
      ctx.closePath();
    };

    canvas.addEventListener("mousedown", startDrawing);
    canvas.addEventListener("mousemove", draw);
    canvas.addEventListener("mouseup", stopDrawing);
    canvas.addEventListener("mouseleave", stopDrawing);

    setCanvasReady(true);

    return () => {
      canvas.removeEventListener("mousedown", startDrawing);
      canvas.removeEventListener("mousemove", draw);
      canvas.removeEventListener("mouseup", stopDrawing);
      canvas.removeEventListener("mouseleave", stopDrawing);
    };
  }, [patientName, currentDigit]);

  const clearCanvas = () => {
    if (!canvasRef.current) return;
    const ctx = canvasRef.current.getContext("2d");
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    setHasDrawn(false);
  };

  const handlePredict = async () => {
    if (!canvasRef.current || !hasDrawn) {
      toast.error("Please draw something first!");
      return;
    }

    try {
      setProcessing(true);
      const dataURL = canvasRef.current.toDataURL("image/png");
      const { predictedDigit, feedbackText } = await mcp.processDrawing(dataURL);
      const correct = parseInt(predictedDigit) === currentDigit;

      setFloatingDigit({ digit: predictedDigit, isCorrect: correct });
      setTimeout(() => setFloatingDigit(null), 1200);

      sessionManager.registerAttempt(predictedDigit, correct, feedbackText);
      logAttempt({
        targetDigit: currentDigit,
        predictedDigit: parseInt(predictedDigit),
        correct,
        feedbackText,
        timestamp: new Date().toISOString(),
      });

      if (correct) {
        successAudio.play();
        toast.success(`✅ Correct! You wrote ${currentDigit}\nFeedback: ${feedbackText}`, {
          duration: 5000,
          style: { whiteSpace: "pre-line" },
        });
        setPredictionCount((prev) => prev + 1);
        onComplete();
      } else {
        toast.error(`❌ Try again! You wrote ${predictedDigit}, not ${currentDigit}.`);
      }

      clearCanvas();
    } catch (error) {
      console.error("Prediction failed:", error);
      toast.error("Something went wrong while predicting. Please try again.");
    } finally {
      setProcessing(false);
    }
  };

  const commonButtonClasses =
    "px-6 py-2 rounded-full text-white font-semibold transition duration-300 transform hover:scale-105 focus:outline-none focus:ring-2";

  return (
    <div className="relative flex flex-col items-center space-y-4 mt-4 w-full max-w-md">
      {/* Temporarily comment tracing overlay to avoid zIndex conflicts */}
      {/* <img
        src={`${process.env.PUBLIC_URL}/tracing/${currentDigit}.png`}
        alt="Tracing overlay"
        className="absolute w-[300px] h-[300px] opacity-20 pointer-events-none"
        style={{ zIndex: 1 }}
      /> */}

      <canvas
        ref={canvasRef}
        className="z-10 rounded-xl border border-gray-300 bg-black cursor-crosshair"
      />

      <div className="flex gap-4">
        <button
          onClick={handlePredict}
          className={`${commonButtonClasses} bg-blue-500 hover:bg-blue-600 focus:ring-blue-300 flex items-center justify-center gap-2`}
          aria-label="Predict the drawn digit"
          disabled={processing || !canvasReady}
        >
          {processing ? (
            <>
              <ClipLoader size={20} color="#fff" />
              Processing...
            </>
          ) : (
            "Predict"
          )}
        </button>

        <button
          onClick={clearCanvas}
          className={`${commonButtonClasses} bg-gray-500 hover:bg-gray-600 focus:ring-gray-300`}
          aria-label="Clear the drawing canvas"
          disabled={processing || !canvasReady}
        >
          Clear
        </button>
      </div>

      {predictionCount > 0 && (
        <p className="text-blue-700 font-medium">
          ✨ Predictions made: <span className="font-bold">{predictionCount}</span>
        </p>
      )}

      {floatingDigit && (
        <div
          className={`absolute text-5xl font-bold select-none pointer-events-none 
            ${floatingDigit.isCorrect ? "text-pink-300 animate-float-up" : "text-red-400 animate-float-down"} 
            left-1/2 transform -translate-x-1/2 top-1/2`}
          style={{ zIndex: 50 }}
        >
          {floatingDigit.digit}
        </div>
      )}
    </div>
  );
}

export default CanvasDrawing;
