import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
  LabelList,
} from "recharts";

export default function ProgressDashboard({
  patientName,
  metrics,
  digitAccuracy,
  strokeSpeedData,
  micrographiaData,
  tremorData,
}) {
  // ✅ Display nothing if any required metric is missing
  if (
    !metrics ||
    digitAccuracy === undefined ||
    !strokeSpeedData?.averageSpeed ||
    !micrographiaData?.shrinkRatio ||
    !tremorData?.tremorScore
  ) {
    return (
      <div className="mt-6">
        <p className="text-sm text-gray-500">
          No metrics available for visualization.
        </p>
      </div>
    );
  }

  const chartData = [
    {
      name: "Accuracy",
      value: Number((digitAccuracy * 100).toFixed(2)),
      unit: "%",
    },
    {
      name: "Speed",
      value: Number(strokeSpeedData.averageSpeed.toFixed(2)),
      unit: "px/s",
    },
    {
      name: "Shrink Ratio",
      value: Number(micrographiaData.shrinkRatio.toFixed(2)),
      unit: "",
    },
    {
      name: "Tremor Score",
      value: Number(tremorData.tremorScore.toFixed(2)),
      unit: "",
    },
  ];

  return (
    <div className="mt-8 w-full max-w-3xl mx-auto p-4 bg-white rounded-2xl shadow-lg">
      <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">
        Session Summary for {patientName}
      </h2>
      <ResponsiveContainer width="100%" height={320}>
        <BarChart data={chartData} margin={{ top: 20, right: 20, left: 0, bottom: 30 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" stroke="#8884d8" />
          <YAxis />
          <Tooltip formatter={(value, name, props) => [`${value} ${props.payload.unit}`, name]} />
          <Bar dataKey="value" fill="#8884d8" radius={[10, 10, 0, 0]}>
            <LabelList dataKey="value" position="top" />
          </Bar>
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
