import React, { useState, useEffect } from "react";
import ResultsTabs from "../components/ResultsTabs";
import ReplaySection from "../components/ReplaySection";
import ProgressDashboard from "../components/ProgressDashboard";
import { generatePDFReport, generateJSONReport } from "../agents/tracking_agent/report_generator";
import { saveAs } from "file-saver";
import { mcp } from "../agents/mcpManager";
import { toast } from "react-hot-toast";

const motivationalQuotes = [
  "Your progress is beautiful. üå∏",
  "You‚Äôre doing better than you think. üåü",
  "Small steps still move you forward. üêæ",
  "Your efforts matter. üíñ"
];

function ResultsPage() {
  const [activeTab, setActiveTab] = useState("core");
  const [quote, setQuote] = useState(motivationalQuotes[0]);
  const [attempts, setAttempts] = useState([]);
  const [metrics, setMetrics] = useState(null);
  const [fatigueInfo, setFatigueInfo] = useState(null);
  const [patientName, setPatientName] = useState("Anonymous");
  const [sessionId, setSessionId] = useState("session_unknown");

  // Rotate quotes every 5 seconds

  useEffect(() => {
    const interval = setInterval(() => {
      const random = Math.floor(Math.random() * motivationalQuotes.length);
      setQuote(motivationalQuotes[random]);
    }, 5000);
    return () => clearInterval(interval);
  }, []);
console.log(" results page", mcp);
  useEffect(() => {
    const endSessionAndLoadData = async () => {
      try {
        const result = await mcp.endSession({ autoDownloadLog: true });

        setAttempts(result.drawings || []);
        setMetrics(result.metrics || {});
        setFatigueInfo(result.fatigueInfo || {});
        setPatientName(mcp.patientName || "Anonymous");
        setSessionId(mcp.sessionId || `session_${Date.now()}`);

        console.log("‚úÖ MCP session ended and ZIP downloaded.");
        toast.success("üì¶ Session ended and ZIP downloaded successfully!");
      } catch (err) {
        console.error("‚ùå Failed to end session:", err);
        toast.error("‚ùå Failed to end session. Please try again.");
      }
    };

    endSessionAndLoadData();
  }, []);



  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-yellow-100 to-pink-100 p-4">
      <div className="text-center bg-white/60 border border-white backdrop-blur-md shadow-sm px-6 py-2 rounded-full text-sm font-medium text-gray-800 animate-pulse mb-6">
        ‚ú® {quote}
      </div>

      <ResultsTabs activeTab={activeTab} setActiveTab={setActiveTab} />

      <div className="mt-4">
        {activeTab === "core" && (
          <div className="overflow-x-auto bg-white rounded-lg shadow p-4">
            <table className="min-w-full text-sm text-left">
              <thead className="bg-blue-100 text-blue-800">
                <tr>
                  <th className="px-4 py-2">Target</th>
                  <th className="px-4 py-2">Prediction</th>
                  <th className="px-4 py-2">Correct?</th>
                  <th className="px-4 py-2">Feedback</th>
                  <th className="px-4 py-2">Time</th>
                </tr>
              </thead>
              <tbody>
                {attempts.map((a, i) => (
                  <tr key={i} className="border-b">
                    <td className="px-4 py-2">{a.targetDigit}</td>
                    <td className="px-4 py-2">{a.predictedDigit}</td>
                    <td className="px-4 py-2">{a.correct ? "‚úÖ" : "‚ùå"}</td>
                    <td className="px-4 py-2">{a.feedbackText}</td>
                    <td className="px-4 py-2">{new Date(a.timestamp).toLocaleTimeString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {activeTab === "feedback" && (
          <div className="bg-white rounded-lg shadow p-4 text-gray-700">
            <p>üß† Feedback & Therapist Suggestions will go here.</p>
          </div>
        )}

        {activeTab === "summary" && (
          <>
            <div className="bg-white rounded-lg shadow p-4 text-gray-800 space-y-2">
              <h2 className="text-xl font-bold text-green-800">üìä Session Summary</h2>
              {metrics ? (
                <ul className="list-disc list-inside space-y-1">
                  <li><strong>Average Speed:</strong> {metrics.avgSpeed?.toFixed(2)} px/s</li>
                  <li><strong>Average Pressure:</strong> {metrics.avgPressure?.toFixed(2)}</li>
                  <li><strong>Stroke Stability:</strong> {metrics.speedVariance?.toFixed(2)}</li>
                  <li><strong>Total Strokes:</strong> {metrics.totalStrokes}</li>
                </ul>
              ) : (
                <p className="text-sm text-gray-500">Loading session summary...</p>
              )}

              {fatigueInfo && (
                <div className="mt-4">
                  <h3 className="font-semibold text-red-700">üõë Fatigue Analysis</h3>
                  <p>Detected: {fatigueInfo.detected ? "Yes" : "No"}</p>
                  <p>Reason: {fatigueInfo.reason}</p>
                </div>
              )}


<ProgressDashboard patientName={patientName} />            </div>

            <ReplaySection
              attempts={attempts}
              mcp={mcp}
              metrics={metrics}
              fatigue={fatigueInfo}
              patientName={patientName}
            />

            <div className="relative w-full h-40 mt-12 overflow-hidden">
              <img src="/assets/grass.png" alt="Grass" className="absolute bottom-0 w-full object-cover z-0" />
              <img src="/assets/Walking_Bird.gif" alt="Bird" className="absolute bottom-4 left-4 h-20 animate-wiggle" style={{ animation: "walk 12s linear infinite" }} />
              <img src="/assets/flowers/BlueFlower.png" className="absolute bottom-4 left-32 h-12 animate-bounce-slow" />
              <img src="/assets/flowers/PinkFlower.png" className="absolute bottom-4 left-64 h-10 animate-float-soft" />
              <img src="/assets/flowers/OrangeFlower.png" className="absolute bottom-4 left-[40%] h-14 animate-bounce-slow" />
              <img src="/assets/flowers/PurpleFlower.png" className="absolute bottom-4 right-32 h-12 animate-float-soft" />
              <img src="/assets/flowers/YellowFlower.png" className="absolute bottom-4 right-64 h-10 animate-bounce-slow" />
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default ResultsPage;
