import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";

export default function ProgressDashboard({
  patientName,
  metrics,
  digitAccuracy,
  strokeSpeedData,
  micrographiaData,
  tremorData
}) {
  // üõ°Ô∏è Guard: prevent crash if any data is missing
  if (!metrics || !strokeSpeedData || !micrographiaData || !tremorData) {
    return (
      <div className="mt-6 text-sm text-gray-500">
        ‚ö†Ô∏è Metrics not available. Please complete a session first.
      </div>
    );
  }

  // ‚úÖ Safe data structure
  const data = [
    {
      label: "Accuracy (%)",
      value: digitAccuracy ? (digitAccuracy * 100).toFixed(2) : "N/A",
    },
    {
      label: "Avg Speed",
      value: strokeSpeedData?.averageSpeed?.toFixed(2) ?? "N/A",
    },
    {
      label: "Shrink Ratio",
      value: micrographiaData?.shrinkRatio?.toFixed(2) ?? "N/A",
    },
    {
      label: "Tremor Score",
      value: tremorData?.tremorScore?.toFixed(2) ?? "N/A",
    },
  ];

  return (
    <div className="mt-6 p-4 rounded-lg bg-white shadow">
      <h2 className="text-lg font-semibold text-gray-700 mb-4">
        üß† Session Summary for <span className="text-blue-700">{patientName}</span>
      </h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data} margin={{ top: 20, right: 20, left: 10, bottom: 20 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="label" />
          <YAxis />
          <Tooltip />
          <Bar dataKey="value" fill="#4B9CD3" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
