// src/components/ReplaySection.jsx
//need to get to it later for the replay speed/ smoother replay effects/ enhancments/Move replay to a modal or separate section.
import React, { useEffect, useState, useRef } from "react";
import { saveAs } from "file-saver";
import { generatePDFReport, generateJSONReport } from "../agents/tracking_agent/report_generator";

function ReplaySection({ attempts = [], metrics, fatigue, patientName }) {
  const [selectedReplay, setSelectedReplay] = useState(null);
  const canvasRef = useRef(null);

  const replay = (strokes) => {
    const canvas = canvasRef.current;
    if (!canvas || !strokes) return;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.strokeStyle = "#4B5563";
    ctx.lineWidth = 4;
    ctx.lineCap = "round";

    let i = 0;
    const drawStep = () => {
      if (i >= strokes.length) return;
      const { x, y, type } = strokes[i];
      if (type === "start") {
        ctx.beginPath();
        ctx.moveTo(x, y);
      } else if (type === "draw") {
        ctx.lineTo(x, y);
        ctx.stroke();
      } else if (type === "end") {
        ctx.closePath();
      }
      i++;
      setTimeout(drawStep, 20);
    };
    drawStep();
  };

  useEffect(() => {
    if (selectedReplay) {
      replay(selectedReplay.strokes);
    }
  }, [selectedReplay]);

  const handleDownloadPDF = async () => {
    if (!metrics || !fatigue) return alert("📊 Metrics not available!");
    const doc = await generatePDFReport(metrics, fatigue, {
      autoSave: false,
      patientName,
    });
    doc.save(`therapy_session_${new Date().toISOString()}.pdf`);
  };

  const handleDownloadJSON = async () => {
    if (!metrics || !fatigue) return alert("📊 Metrics not available!");
    const blob = await generateJSONReport(metrics, fatigue);
    saveAs(blob, `therapy_session_${new Date().toISOString()}.json`);
  };

  return (
    <div className="mt-10 bg-white/70 border border-gray-300 p-6 rounded-xl shadow-md backdrop-blur-sm">
      <h3 className="text-lg font-semibold text-gray-700 mb-3">
        🌿 Would you like to save or replay your attempts?
      </h3>

      <div className="flex flex-wrap gap-2 mb-4">
        {attempts.map((attempt, index) => (
          <button
            key={index}
            onClick={() => setSelectedReplay(attempt)}
            className={`px-3 py-1 rounded-lg text-sm font-medium border shadow-sm transition ${
              selectedReplay === attempt
                ? "bg-blue-500 text-white"
                : "bg-gray-100 hover:bg-gray-200"
            }`}
          >
            {attempt.targetDigit} → {attempt.predictedDigit} {attempt.correct ? "✅" : "❌"}
          </button>
        ))}
      </div>

      {selectedReplay && (
        <div className="mb-6">
          <h4 className="font-semibold text-blue-700 mb-2">
            🔁 Replaying digit: {selectedReplay.targetDigit}
          </h4>
          <canvas
            ref={canvasRef}
            width={300}
            height={300}
            className="border rounded bg-gray-100"
          />
        </div>
      )}

      <div className="flex flex-wrap gap-4 mt-4">
        <button
          onClick={handleDownloadPDF}
          className="bg-blue-200 hover:bg-blue-300 text-blue-900 px-4 py-2 rounded-lg font-medium shadow"
        >
          📄 Download PDF
        </button>
        <button
          onClick={handleDownloadJSON}
          className="bg-yellow-200 hover:bg-yellow-300 text-yellow-900 px-4 py-2 rounded-lg font-medium shadow"
        >
          🧾 Download JSON
        </button>
      </div>
    </div>
  );
}

export default ReplaySection;
