// agents/tracking_agent/TherapySessionAgent.js
export default class TherapySessionAgent {
    constructor(patientName = "Anonymous") {
      this.patientName = patientName;
      this.digits = [...Array(10).keys()];
      this.currentIndex = 0;
      this.attempts = [];
      this.startTime = new Date().toISOString();
    }
  
    getCurrentDigit() {
      return this.digits[this.currentIndex];
    }
  
    registerAttempt(predicted, correct, feedbackText) {
      this.attempts.push({
        target: this.getCurrentDigit(),
        predicted,
        correct,
        feedbackText,
        time: new Date().toISOString(),
      });
    }
  
    goToNextDigit() {
      if (this.currentIndex < this.digits.length - 1) {
        this.currentIndex++;
      }
    }
  
    isSessionFinished() {
      return this.currentIndex >= this.digits.length - 1;
    }
  
    getSessionSummary() {
      return {
        patientName: this.patientName,
        startTime: this.startTime,
        attempts: this.attempts,
      };
    }
  }
