// ✅ frontend/src/agents/tracking_agent/session_logger.js

const STORAGE_KEY = "attempts";

/**
 * Logs a single drawing attempt into localStorage.
 * Each attempt contains:
 * - targetDigit
 * - predictedDigit
 * - correct
 * - feedbackText
 * - timestamp
 * - patientName
 */
export function logAttempt(metricsObj) {
  const existing = JSON.parse(localStorage.getItem(STORAGE_KEY) || "[]");
  existing.push(metricsObj);
  localStorage.setItem(STORAGE_KEY, JSON.stringify(existing));
}

/**
 * Retrieves all stored drawing attempts from localStorage.
 */
export function getAllAttempts() {
  return JSON.parse(localStorage.getItem(STORAGE_KEY) || "[]");
}

/**
 * Clears all stored attempts from localStorage.
 */
export function clearAttempts() {
  localStorage.removeItem(STORAGE_KEY);
}
