import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";

export default function ProgressDashboard({
  patientName,
  metrics,
  digitAccuracy,
  strokeSpeedData,
  micrographiaData,
  tremorData
}) {
  if (!metrics || !strokeSpeedData || !micrographiaData || !tremorData) {
    return (
      <div className="mt-6">
        <p className="text-sm text-gray-500">No metrics available for visualization.</p>
      </div>
    );
  }

  const data = [
    { label: 'Accuracy (%)', value: (digitAccuracy * 100).toFixed(2) },
    { label: 'Avg Speed', value: strokeSpeedData.averageSpeed.toFixed(2) },
    { label: 'Shrink Ratio', value: micrographiaData.shrinkRatio.toFixed(2) },
    { label: 'Tremor Score', value: tremorData.tremorScore.toFixed(2) },
  ];

  return (
    <div className="mt-8 bg-white p-4 rounded-lg shadow-md">
      <h3 className="text-lg font-bold text-gray-800 mb-4">
        ðŸ“ˆ Visual Progress Summary for <span className="text-blue-600">{patientName}</span>
      </h3>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="label" />
          <YAxis />
          <Tooltip />
          <Bar dataKey="value" fill="#38bdf8" radius={[4, 4, 0, 0]} />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
